import styled from 'styled-components';

export const FooterSection = styled.div`
text-align: center;
padding: 20px;
background: #f8f8ff;
`
export const Paragraph = styled.p`
color: #534F80;
margin: 0;
font-weight: bold;
`
export const ScrollTop = styled.div`
width: 50px;
height: 50px;
text-align: center;
position: fixed;
right: 15px;
bottom: 10px;
z-index: 9999;

cursor: pointer;
`
