/* Second way to Styled Components*/

import styled from 'styled-components';

export const NavbarSection = styled.div`
padding: 20px 0;
overflow: hidden;
position: relative;
`

export const Logo = styled.div`
/* width: 50%;*/
float: left;
@media (max-width : 991px){
    .navbar .logo{
        width:100%;
        float: none;
    }
`

export const LogoText = styled.h2`
font-size : 30px;
font-weight: bold;
`
export const NavbarInput= styled.input`
position: absolute;
top: 15px;
right: 25px;
display: none;
&:checked ~ UlList{
    display: block;
}
&:checked ~  NavbarLabel{
    /*background: url("./../../../public/images/Navbar/close.png") no-repeat;*/
    background-size: cover;
}
`
export const NavbarLabel= styled.label`
position: absolute;
top: 15px;
right: 25px;
display: block;
width: 15px;
height: 15px;
/*background: url("../images/Navbar/open.png") no-repeat;*/
/*background: url("./../../../public/images/Navbar/open.png");*/
background-size: cover;
padding: 20px;
cursor: pointer;
`

export const UlList = styled.ul`
width: 50%;
float: right;
list-style: none;
`
export const ListItem = styled.li`
display: inline-block;
`
export const Anchor = styled.a`
display: block;
text-decoration: none !important;
color:#222;
font-weight: bold;
padding: 10px 15px;
&:hover{  
    color: #081F62;
}

`
